import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import { useNavigate } from "react-router-dom";
import "./Services.css";

const Services = () => {
  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);
  const [filteredCategories, setFilteredCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState();
  const [selectedsubCategory, setselectedsubCategory] = useState();
  const [show, setShow] = useState(false);
  const [searchText, setSearchText] = useState("");

  // ✅ Fetch all categories on mount
  useEffect(() => {
    fetch("http://localhost:5000/categories")
      .then((response) => response.json())
      .then((data) => {
        console.log("categories Data:", data);
        setCategories(data);
        setFilteredCategories(data); // Set for initial load
      })
      .catch((error) => console.error("Error fetching categories:", error));
  }, []);

  // ✅ Filter categories based on search text
  useEffect(() => {
    if (searchText.trim() === "") {
      setFilteredCategories(categories);
    } else {
      const filtered = categories.filter((category) =>
        category.name.toLowerCase().includes(searchText.toLowerCase()) ||
        category.subcategories.some((sub) =>
          sub.subcategoryname.toLowerCase().includes(searchText.toLowerCase())
        )
      );
      setFilteredCategories(filtered);
    }
  }, [searchText, categories]);

  const handleShow = (category) => {
    setSelectedCategory(category);
    setShow(true);
  };

  const handlesubcategory = (subs) => {
    setselectedsubCategory(subs);
    navigate(`/viewsmallsubcategory/${selectedCategory?.name}/${subs.subcategoryname}`);
  };

  const handleClose = () => setShow(false);

  return (
    <div className="services-container mt-5 p-4">
      {/* ✅ Search Bar */}
      <div className="container mb-4">
        <input
          type="text"
          className="form-control"
          placeholder="Search categories or subcategories..."
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
        />
      </div>

      {/* ✅ Categories Display */}
      <div className="categories-container">
        {filteredCategories.length > 0 ? (
          filteredCategories.map((category) => (
            <div
              key={category._id}
              className="category-card"
              onClick={() => handleShow(category)}
            >
              <strong>{category.name}</strong>
              <img src={category.image} alt={category.name} />
            </div>
          ))
        ) : (
          <p className="text-center">No categories or subcategories match your search.</p>
        )}
      </div>

      {/* ✅ Modal for Subcategories */}
      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>{selectedCategory?.name} - Subcategories</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedCategory && selectedCategory.subcategories.length > 0 ? (
            <ul className="list-group">
              {selectedCategory.subcategories.map((sub) => (
                <li
                  key={sub._id}
                  className="list-group-item d-flex justify-content-between align-items-center"
                  onClick={() => handlesubcategory(sub)}
                  style={{ cursor: "pointer" }}
                >
                  {sub.subcategoryname}
                  <img
                    src={sub.subcategoryimage}
                    alt={sub.subcategoryname}
                    style={{ width: "50px", height: "50px" }}
                  />
                </li>
              ))}
            </ul>
          ) : (
            <p>No subcategories available.</p>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Services;
