import React, { useState, useEffect } from "react";
import { Outlet, Link, useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap-icons/font/bootstrap-icons.css";

const Navbar = () => {
  const [placeholderIndex, setPlaceholderIndex] = useState(0);
  const [username, setUsername] = useState(""); // ✅ Fixed Initial State
  const navigate = useNavigate();

  useEffect(() => {
    const user = localStorage.getItem("name");
    if (user) {
      setUsername(user); // ✅ Set username
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("name");
    setUsername(""); // ✅ Fix: Reset to empty string
    navigate("/login");
  };

  useEffect(() => {
    const userToken = localStorage.getItem("token");
    if (!userToken) {
      setTimeout(() => navigate("/login"), 100);
    }

    const placeholders = [
      "Search for 'Facial'",
      "Search for 'Kitchen Cleaning'",
      "Search for 'Home Spa'",
      "Search for 'Plumbing'",
      "Search for 'AC Repair'",
    ];

    const interval = setInterval(() => {
      setPlaceholderIndex((prevIndex) => (prevIndex + 1) % placeholders.length);
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <nav
        className="navbar navbar-expand-lg"
        style={{
          background: "linear-gradient(to right, #F8F9FA, #E3E7EB)",
          boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
          padding: "12px 25px",
        }}
      >
        <div className="container-fluid">
          <a className="navbar-brand fw-bold" href="#">
            <img
              src="https://res.cloudinary.com/urbanclap/image/upload/t_high_res_category/w_108,dpr_2,fl_progressive:steep,q_auto:low,f_auto,c_limit/images/growth/home-screen/1687285683825-e6cf23.jpeg"
              alt="Logo"
              height="40"
              style={{ borderRadius: "8px" }}
            />
          </a>

          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse justify-content-center" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item px-3">
                <Link to="/frontblog" className="nav-link fw-bold">Blogs</Link>
              </li>
              <li className="nav-item px-3">
                <Link to="/" className="nav-link fw-bold">Category</Link>
              </li>
            </ul>
          </div>

          <div className="d-flex align-items-center">
            <Link to="viewAddtocart" className="me-4" style={{ fontSize: "20px" }}>
              <i className="bi bi-cart3"></i>
            </Link>

            {username ? (
              <div className="d-flex align-items-center">
                <span className="user-circle">{username?.charAt(0)?.toUpperCase()}</span>
                <button className="btn btn-danger ms-3" onClick={handleLogout}>
                  Logout
                </button>
              </div>
            ) : (
              <Link to="/SignUp" style={{ fontSize: "20px" }}>
                <i className="bi bi-person-circle"></i>
              </Link>
            )}
          </div>
        </div>
      </nav>
      <Outlet />
    </>
  );
};

export default Navbar;
