import { useEffect, useState } from "react";

const Addtocart = () => {
  const [cart, setCart] = useState([]);
  const [orderPlace, setorderPlace] = useState(false);
  const [cartcount, setcartcount] = useState(0);
  const [showOrders, setShowOrders] = useState(false);

  const [myOrders, setMyOrders] = useState([]);

  const email = localStorage.getItem("email") || "guest";

  const fetchOrders = async () => {
    const userId = localStorage.getItem("USER");
    if (!userId) return;

    try {
      const res = await fetch(`http://localhost:5000/api/order/user/${userId}`);

      const data = await res.json();
      console.log("Fetched Orders from backend: ", data);
      setMyOrders(data);
    } catch (err) {
      console.error("Failed to fetch orders", err);
    }
  };

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem(`cart_${email}`)) || [];
    setCart(storedCart);
    setcartcount(storedCart.length);
    // fetchOrders(); // 👈 isse yahan bhi call karo, tabhi page load pe orders aayenge

  }, [email]);

  const updateCart = (updatedCart) => {
    setCart(updatedCart);
    setcartcount(updatedCart.length);
    localStorage.setItem(`cart_${email}`, JSON.stringify(updatedCart));
    localStorage.setItem("cartcount", updatedCart.length);
    window.dispatchEvent(new Event("storage"));
  };

  const handleIncreaseQuantity = (index) => {
    const updatedCart = cart.map((item, i) =>
      i === index
        ? {
            ...item,
            quantity: item.quantity + 1,
            price: (item.quantity + 1) * (item.price / item.quantity),
          }
        : item
    );
    updateCart(updatedCart);
  };

  const handleDecreaseQuantity = (index) => {
    const updatedCart = cart
      .map((item, i) =>
        i === index && item.quantity > 1
          ? {
              ...item,
              quantity: item.quantity - 1,
              price: (item.quantity - 1) * (item.price / item.quantity),
            }
          : item
      )
      .filter((item) => item.quantity > 0);

    updateCart(updatedCart);
  };

  const handleRemoveItem = (index) => {
    const updatedCart = cart.filter((_, i) => i !== index);
    updateCart(updatedCart);
  };

  const totalAmount = cart
    .reduce((total, item) => total + parseFloat(item.price), 0)
    .toFixed(2);

  const handlePlaceOrder = async () => {
    const userId = localStorage.getItem("USER");

    if (!userId || cart.length === 0) {
      alert("Invalid order data. Please log in and add items.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/order", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          userId,
          cartItems: cart,
          totalAmount,
        }),
      });

      const data = await response.json();

      if (response.status === 201) {
        alert(data.message);

        // ✅ Reset cart
        localStorage.removeItem(`cart_${email}`);
        localStorage.setItem("cartcount", 0);
        setCart([]);
        setcartcount(0);
        setorderPlace(true);

        // ✅ Fetch updated orders
        fetchOrders();
      } else {
        alert(data.message || "Order not placed");
      }
    } catch (error) {
      alert("Something went wrong. Please try again.");
      console.error("Order placement error", error);
    }
  };

  return (
    <div className="container my-5">
      <style>{`
        body {
          font-family: 'Segoe UI', sans-serif;
        }

        .cart-box {
          border-radius: 16px;
          background: rgba(255, 255, 255, 0.9);
          box-shadow: 0 8px 16px rgba(0,0,0,0.1);
          padding: 20px;
          transition: transform 0.2s ease;
        }

        .cart-box:hover {
          transform: translateY(-5px);
        }

        .cart-image {
          height: 180px;
          object-fit: cover;
          border-radius: 12px;
          width: 100%;
        }

        .btn-custom {
          border-radius: 20px;
          padding: 4px 12px;
          font-weight: bold;
          border: none;
          transition: all 0.2s ease;
        }

        .btn-custom:hover {
          opacity: 0.9;
          transform: scale(1.05);
        }

        .btn-increase {
          background-color: #00b894;
          color: white;
        }

        .btn-decrease {
          background-color: #fdcb6e;
          color: white;
        }

        .btn-remove {
          background-color: #d63031;
          color: white;
        }

        .order-summary-box {
          border-radius: 16px;
          background: linear-gradient(to top left, #f5f5f5, #ffffff);
          padding: 25px;
          box-shadow: 0 6px 14px rgba(0,0,0,0.12);
        }

        .total-price {
          font-size: 1.2rem;
          color: #2ecc71;
        }

        .btn-order {
          border-radius: 30px;
          font-weight: bold;
          padding: 12px;
          background: linear-gradient(to right, #6a11cb, #2575fc);
          border: none;
          color: white;
          transition: all 0.3s ease;
        }

        .btn-order:hover {
          opacity: 0.95;
          transform: scale(1.02);
        }

        h2 {
          font-weight: 600;
          color: #2d3436;
        }
      `}</style>

      <h2 className="text-center mb-4">🛒 Your Cart</h2>
      {orderPlace && (
        <div className="alert alert-success text-center">✅ Order Placed</div>
      )}

      {cart.length === 0 ? (
        <p className="text-center">Your cart is empty.</p>
      ) : (
        <div className="row">
          <div className="col-md-8">
            <div className="row">
              {cart.map((item, index) => (
                <div key={index} className="col-md-6 mb-4">
                  <div className="cart-box">
                    <img
                      src={item.image}
                      className="cart-image"
                      alt={item.name}
                    />
                    <h5 className="mt-3">{item.name}</h5>
                    <p>Price: ₹{item.price}</p>
                    <p>Quantity: {item.quantity}</p>
                    <div className="d-flex justify-content-between">
                      <button
                        className="btn-custom btn-increase"
                        onClick={() => handleIncreaseQuantity(index)}
                      >
                        +
                      </button>
                      <button
                        className="btn-custom btn-decrease"
                        onClick={() => handleDecreaseQuantity(index)}
                      >
                        -
                      </button>
                      <button
                        className="btn-custom btn-remove"
                        onClick={() => handleRemoveItem(index)}
                      >
                        Remove
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Order Summary */}
          <div className="col-md-4">
            <div className="order-summary-box">
              <h5 className="mb-3">🧾 Order Summary</h5>
              <ul className="list-group list-group-flush">
                {cart.map((item, idx) => (
                  <li
                    key={idx}
                    className="list-group-item d-flex justify-content-between align-items-center"
                  >
                    <span>
                      {item.name} × {item.quantity}
                    </span>
                    <span className="fw-bold">₹{item.price}</span>
                  </li>
                ))}
                <li className="list-group-item d-flex justify-content-between fw-bold border-top pt-3">
                  <span>Total</span>
                  <span className="total-price">₹{totalAmount}</span>
                </li>
              </ul>
              <button
                className="btn-order w-100 mt-3"
                onClick={handlePlaceOrder}
              >
                🛍️ Place Order
              </button>
            </div>
          </div>
        </div>
      )}
      {/* View My Orders Button */}
<div className="text-center mt-5">
  <button
    className="btn btn-outline-primary"
    onClick={() => {
      fetchOrders();
      setShowOrders(true);
    }}
  >
    📦 View My Past Orders
  </button>
</div>

{/* My Orders Section */}
{showOrders && myOrders.length > 0 && (
  <div className="my-5">
    <h3 className="text-center mb-3">📦 My Past Orders</h3>
    {myOrders.map((order, index) => (
      <div key={index} className="card mb-3 p-3 shadow-sm rounded">
        <h5>Order #{index + 1}</h5>
        <p><strong>Date:</strong> {new Date(order.orderDate).toLocaleString()}</p>
        <p><strong>Total:</strong> ₹{order.totalAmount}</p>
        <p><strong>Payment:</strong> {order.paymentStatus}</p>
        <p><strong>Delivery:</strong> {order.deliveryStatus}</p>
        <ul className="mb-2">
          {order.cartItems.map((item, i) => (
            <li key={i}>{item.name} × {item.quantity} = ₹{item.price}</li>
          ))}
        </ul>
      </div>
    ))}
  </div>
)}

    </div>

  );
};

export default Addtocart;
