import React from "react";
import "./slider.css"; // Optional CSS for styling

const Slider = () => {
  const slides = [
    {
      id: 1,
      image: "https://via.placeholder.com/900x300?text=Affordable+Home+Services",
      caption: "Affordable Home Services at Your Doorstep"
    },
    {
      id: 2,
      image: "https://via.placeholder.com/900x300?text=Beauty+%26+Wellness",
      caption: "Beauty & Wellness Packages for You"
    },
    {
      id: 3,
      image: "https://via.placeholder.com/900x300?text=Expert+Technicians",
      caption: "Expert Technicians. Guaranteed Quality."
    }
  ];

  return (
    <div className="slider-container">
      {slides.map((slide) => (
        <div key={slide.id} className="slide">
          <img src={slide.image} alt={slide.caption} className="slide-image" />
          <p className="slide-caption">{slide.caption}</p>
        </div>
      ))}
    </div>
  );
};

export default Slider;
