import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const Viewsmallsubcategory = () => {
  const navigate = useNavigate();
  const { name, subcategoryname } = useParams();
  const [smallsubcategories, setSmallSubcategories] = useState([]);
  const [cart, setCart] = useState([]);

  const userId = localStorage.getItem("USER") || "guest"; // User ID from login

  useEffect(() => {
    axios.get(`http://localhost:8000/getsmallsubcategory/${name}/${subcategoryname}`)
      .then(response => setSmallSubcategories(response.data))
      .catch(error => console.error("Error fetching data:", error));

    // Load cart from localStorage
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, [name, subcategoryname]);

  const handleAddToCart = (item) => {
    const existingItemIndex = cart.findIndex(
      (cartItem) => cartItem.productId === item._id && cartItem.userId === userId && cartItem.status === "pending"
    );

    let updatedCart;
    if (existingItemIndex !== -1) {
      // Agar item pehle se hai, toh quantity aur price update karo
      updatedCart = cart.map((cartItem, index) =>
        index === existingItemIndex
          ? {
              ...cartItem,
              quantity: cartItem.quantity + 1,
              price: (cartItem.quantity + 1) * item.price, // Price update ho raha hai
            }
          : cartItem
      );
    } else {
      // Naya item add karo
      updatedCart = [
        ...cart,
        {
          productId: item._id,
          userId: userId,
          name: item.subsmallcategoryname,
          image: item.subsmallcategoryimage,
          price: item.price,
          quantity: 1,
          status: "pending",
        },
      ];
    }

    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));

    alert("Item added to cart!");
    navigate("/addtocart");
  };

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">{subcategoryname} Products</h2>

      <div className="row">
        {smallsubcategories.map((item) => (
          <div className="col-md-4 mb-4" key={item._id}>
            <div className="card">
              <img src={item.smallsubcategoryimage} className="card-img-top" alt={item.subsmallcategoryname} />
              <div className="card-body">
                <h5>{item.subsmallcategoryname}</h5>
                <p>Price: ₹{item.price}</p>
                <button className="btn btn-primary w-100" onClick={() => handleAddToCart(item)}>
                  Add to Cart
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Viewsmallsubcategory;
