import React, { useState } from "react";
import {Link} from "react-router-dom";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate=useNavigate()
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const obj = JSON.stringify({
      email: email,
      password: password
    });
    console.log(obj);

    try {
      const response = await fetch("http://localhost:5000/api/user", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: obj
      });


      if (response.ok) {
        const data = await response.json(); 
        alert("Successfully login");

        localStorage.setItem("token", data.token); 
        localStorage.setItem("name", data.name);
        localStorage.setItem("email", data.email); 
        navigate("/dashboard");
      }
      


    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div style={{ width: "300px", margin: "100px auto", padding: "20px", textAlign: "center", border: "1px solid #ccc", borderRadius: "10px",backgroundColor:"pink" }}>
      <h2>Login</h2>
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column" }}>
        <input
          type="email"
          placeholder="Enter Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={{ margin: "10px 0", padding: "10px", fontSize: "16px" }}
        />
        <input
          type="password"
          placeholder="Enter Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ margin: "10px 0", padding: "10px", fontSize: "16px" }}
        />
        <button type="submit" style={{ background: "blue", color: "white", padding: "10px", fontSize: "16px", cursor: "pointer" }}>
          Login
        </button>
        <Link to="/Login">Don't have an Account<span style={{fontSize:"20px"}}>  Sign Up</span></Link>
        <Link to="/forgot-password">Forgot Password?</Link>

      </form>
    </div>
  );
};

export default Login;
