import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import pic1 from "../Images/right.webp"; // ✅ Correct Path

const Home = () => {
  return (
    <div className="container mt-5">
      <div className="row align-items-center">
        {/* Left Column (Services Box) */}
        <div className="col-md-5 d-flex flex-column justify-content-center p-4 bg-light rounded shadow-sm" style={{ maxWidth: "450px" }}>
          <h2 className="mb-3">Home services at your doorstep</h2>
          <h5 className="mb-3">What are you looking for?</h5>

          <div className="d-flex flex-wrap justify-content-between">
            {[
              { img: "https://cdn-icons-png.flaticon.com/512/2867/2867281.png", text: "Women's Salon & Spa" },
              { img: "https://cdn-icons-png.flaticon.com/512/2867/2867316.png", text: "Men's Salon & Massage" },
              { img: "https://cdn-icons-png.flaticon.com/512/2867/2867294.png", text: "AC & Appliance Repair" },
              { img: "https://cdn-icons-png.flaticon.com/512/2867/2867294.png", text: "Cleaning" },
              { img: "https://cdn-icons-png.flaticon.com/512/2867/2867294.png", text: "Electrician, Plumber & Carpenter" },
              { img: "https://cdn-icons-png.flaticon.com/512/2867/2867294.png", text: "Water Purifier Services" },
              { img: "https://cdn-icons-png.flaticon.com/512/2867/2867294.png", text: "Smart Locks Installation" },
              { img: "https://cdn-icons-png.flaticon.com/512/2867/2867294.png", text: "Painting & Renovation" },
              { img: "https://cdn-icons-png.flaticon.com/512/2867/2867294.png", text: "Pest Control" },
            ].map((service, index) => (
              <div key={index} className="d-flex flex-column align-items-center p-2 bg-white rounded shadow-sm m-2" style={{ width: "120px" }}>
                <img src={service.img} alt={service.text} width="40" height="40" />
                <span className="text-center" style={{ fontSize: "12px" }}>{service.text}</span>
              </div>
            ))}
          </div>
        </div>

        {/* Right Column (Images Grid) */}
        <div className="col-md-6 d-flex flex-wrap gap-2 justify-content-center">
          {["right1.webp", "right2.webp", "right3.webp", "right4.webp"].map((img, index) => (
            <img
              key={index}
              src={`../Images/${img}`}
              alt="Service Image"
              className="rounded shadow-sm"
              style={{ width: "48%", height: "180px", objectFit: "cover" }}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
