import React, { useEffect, useState } from "react"; 
import "../Blogform.css";  // Adjust relative path
import axios from "axios";
import { useParams } from "react-router-dom";

const Updatecategory = () => {
  const [name, setName] = useState("");  
  const [image, setImage] = useState(""); 
  const [imageURL, setImageURL] = useState("");
  const [items, setItems] = useState({});

  let { id } = useParams();

  // ðŸ›  Fetch category data based on ID and auto-fill fields
  useEffect(() => {
    const fetchCategory = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/getcategory/${id}`);
        if (response.status === 200) {
          const data = response.data;
          setItems(data);
          setName(data.name || "");  // ðŸ›  Auto-fill category name
          setImageURL(data.image || ""); // ðŸ›  Auto-fill category image
        }
      } catch (error) {
        console.error("Error fetching category:", error);
        alert("Failed to load category details");
      }
    };

    fetchCategory();
  }, [id]);

  // ðŸ›  Handle Image Upload
  useEffect(() => {
    if (image) {
      uploadImage(image);
    }
  }, [image]);

  const uploadImage = async (image) => {
    if (!image) {
      alert("Please select an image");
      return;
    }

    const data = new FormData();
    data.append("file", image);
    data.append("upload_preset", "categories");
    data.append("cloud_name", "dbuk7btza");

    await fetch("https://api.cloudinary.com/v1_1/dbuk7btza/image/upload", {
      method: "POST",
      body: data,
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.url) {
          setImageURL(data.url);
          localStorage.setItem("image", data.url);
          alert("Image uploaded successfully");
        } else {
          alert("Failed to upload image");
        }
      })
      .catch((err) => {
        console.error("Upload error:", err);
        alert("Error uploading image");
      });
  };

  // ðŸ›  Handle Submit Form
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !imageURL) {
      alert("All fields are required");
      return;
    }

    alert("Updating Category...");

    try {
      const formData = { id, name, image: imageURL };

      const response = await axios.post(
        "http://localhost:5000/api/updatecategory",
        formData,
        { headers: { "Content-Type": "application/json" } }
      );

      if (response.status === 200) {
        alert("Category updated successfully");
        window.location.href = "/admin/viewcategories";
        setName("");
        setImage("");
      }
    } catch (error) {
      console.error("Error updating category:", error);
      alert("Failed to update category");
    }
  };

  return (
    <div className="comment-container container mt-5 mb-4">
      <div className="row justify-content-center m-4">
        <div className="col-lg-6">
          <div className="card px-4 py-3 shadow-sm">
            <h2 className="text-center mb-3">Update Category</h2>
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label className="form-label">Category Name</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Enter category name"
                  value={name} // âœ… Auto-filled category name
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Image</label>
                <input
                  type="file"
                  className="form-control"
                  onChange={(e) => setImage(e.target.files[0])}
                />
                {imageURL && (
                  <img src={imageURL} alt="Category" className="img-fluid mt-2 rounded shadow" width="100" />
                )}
              </div>

              <button type="submit" className="btn btn-primary w-100">
                Update Category
              </button>
            </form>
          </div>
        </div>

        {/* Category Image Preview */}
        <div className="col-lg-6 mt-3 d-flex flex-column ">
          <img style={{height:"300px",width:"400px"}}
            src="https://th.bing.com/th/id/OIP.wWghX8rRvtrHuA3cGqqjFwHaDt?rs=1&pid=ImgDetMain"
            alt="Blog"
            className="category-image img-fluid rounded shadow "
          />
          <img  style={{height:"300px",width:"400px"}}
            src="https://wpupdoot.com/wp-content/uploads/2021/10/NCT025-Add-Category-1030x579.png"
            alt="Blog"
            className="category-image img-fluid rounded shadow mt-4"
          />
        </div>
      </div>
    </div>
  );
};

export default Updatecategory;
