import React, {  useState, useEffect } from 'react'
import { Link , Outlet, useNavigate} from 'react-router-dom'



const UserLayout = () => {
    const [item,setItem]=useState({})
    const [pic,setPic]=useState("");
    const navigate=useNavigate();


    useEffect(() => {
        // Bootstrap dropdown enable karne ke liye
        const dropdownElements = document.querySelectorAll('.dropdown-toggle');
        dropdownElements.forEach(dropdown => {
          new window.bootstrap.Dropdown(dropdown);
        });
    
        // Get user email from localStorage
        const email = localStorage.getItem("email");
        const profile = localStorage.getItem("profile");
        const name = localStorage.getItem("name");

        if (name) {
            setItem({ name });  // ðŸ‘ˆ This sets name from localStorage for instant rendering
          }
        
        if (email) {
          getUserProfile(email);
          if (profile) {
            setPic(profile);
          }
        }
      }, []);

      const getUserProfile = async (email) => {
        try {
          const res = await fetch("http://localhost:5000/api/get-user", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({ email }),
          });
    
          if (res.ok) {
            const data = await res.json();
            setItem({ name: data.name });
            
          } else {
            console.error("User not found or error in fetching");
          }
        } catch (error) {
          console.error("Fetch user error:", error);
        }
      };

      const handleSignOut = () => {
        // LocalStorage se data remove karna
        localStorage.removeItem("email");
        localStorage.removeItem("role");
        localStorage.removeItem("profile");
    
        // User ko login page pe redirect karna
        navigate("/login");
      };

      return (
        <div className="container-fluid">
          <div className="row flex-nowrap">
            <div className="col-auto col-md-3 col-xl-2 px-sm-2 px-0 bg-primary sidebar-custom">
              <div className="d-flex flex-column align-items-center align-items-sm-start px-3 pt-3 text-white min-vh-100">
                <a href="/" className="d-flex align-items-center pb-3 mb-md-0 me-md-auto text-white text-decoration-none fs-4 fw-bold">
                  <i className="bi bi-person-circle me-2"></i>
                  <span className="d-none d-sm-inline">User Panel</span>
                </a>
                <ul className="nav nav-pills flex-column mb-sm-auto mb-0 w-100" id="menu">
                  <li>
                    <Link to="home" className="nav-link text-white custom-link">
                      <i className="bi bi-house-door me-2"></i> Home
                    </Link>
                  </li>
                  <li>
                    <Link to="orders" className="nav-link text-white custom-link">
                      <i className="bi bi-bag-check me-2"></i> My Orders
                    </Link>
                  </li>
                  <li>
                    <Link to="cart" className="nav-link text-white custom-link">
                      <i className="bi bi-cart me-2"></i> Cart
                    </Link>
                  </li>
                  <li>
                    <Link to="blogs" className="nav-link text-white custom-link">
                      <i className="bi bi-journals me-2"></i> Blogs
                    </Link>
                  </li>
                </ul>
                <hr className="w-100 border-light" />
                <div className="dropdown pb-4 w-100">
                  <a href="#" className="d-flex align-items-center text-white text-decoration-none dropdown-toggle" id="dropdownUser1" data-bs-toggle="dropdown" aria-expanded="false">
                    <img
                      src={pic}
                      alt="Profile"
                      width="32"
                      height="32"
                      className="rounded-circle me-2 shadow-sm"
                    />
                    <span className="d-none d-sm-inline">{user.name}</span>
                  </a>
                  <ul className="dropdown-menu dropdown-menu-dark text-small shadow">
                    <li><Link className="dropdown-item" to="/profile">Profile</Link></li>
                    <li><hr className="dropdown-divider" /></li>
                    <li><button className="dropdown-item" onClick={handleSignOut}>Sign out</button></li>
                  </ul>
                </div>
              </div>
            </div>
            <div className="col py-3 bg-light" style={{ minHeight: '100vh', overflowY: 'auto' }}>
              <Outlet />
            </div>
          </div>
    
          <style jsx>{`
            .sidebar-custom {
              box-shadow: 2px 0 10px rgba(0, 0, 0, 0.2);
            }
    
            .custom-link {
              padding: 10px;
              border-radius: 10px;
              margin: 5px 0;
              transition: background 0.3s ease;
            }
    
            .custom-link:hover {
              background-color: #0d6efd;
              color: #fff !important;
            }
    
            @media (max-width: 768px) {
              .sidebar-custom {
                min-height: auto;
                padding-bottom: 20px;
              }
            }
          `}</style>
        </div>
      );
    };

export default UserLayout
