import React, { useEffect, useState } from "react";
import "../Categoryform.css";
import axios from "axios";

const Subsmallcategories = () => {
  const [subcategoryname, setSubcategoryName] = useState("");
  const [photo, setPhoto] = useState(null);
  const [content, setContent] = useState("");
  const [subcategoryimage, setSubcategoryImage] = useState("");
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [subcategories, setSubcategories] = useState([]); // ✅ Subcategories List
  const [selectedSubcategory, setSelectedSubcategory] = useState(""); // ✅ Selected Subcategory
  const [subSmallCategories, setSubSmallCategories] = useState([]); // ✅ Sub-small Categories List

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const res = await axios.get("http://localhost:5000/categories");
      setCategories(res.data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const fetchSubcategories = async (categoryName) => {
    try {
      const res = await axios.get(`http://localhost:5000/subcategories?category=${categoryName}`);
      setSubcategories(res.data);
    } catch (error) {
      console.error("Error fetching subcategories:", error);
    }
  };

  const uploadToCloudinary = async (photo) => {
    if (!photo) return alert("Please select a photo");

    const data = new FormData();
    data.append("file", photo);
    data.append("upload_preset", "categories");
    data.append("cloud_name", "dbuk7btza");

    try {
      const res = await fetch("https://api.cloudinary.com/v1_1/dbuk7btza/image/upload", {
        method: "POST",
        body: data,
      });
      const file = await res.json();
      if (file.url) {
        setSubcategoryImage(file.url);
        alert("Photo uploaded successfully");
      } else {
        alert("Failed to upload photo");
      }
    } catch (error) {
      console.error("Upload error:", error);
      alert("Error uploading photo");
    }
  };

  useEffect(() => {
    if (photo) uploadToCloudinary(photo);
  }, [photo]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!subcategoryname || !subcategoryimage || !content || !selectedSubcategory) {
      return alert("All fields are required");
    }

    try {
      const formData = { 
        subcategoryname, 
        subcategoryimage, 
        subcategory: selectedSubcategory, 
        content 
      };

      console.log("Sending Data:", formData);

      const response = await axios.post("http://localhost:5000/api/insertsubsmallcategory", formData, {
        headers: { "Content-Type": "application/json" },
      });

      if (response.status === 200) {
        alert("Sub-small Category added successfully");
        setSubcategoryName("");
        setPhoto(null);
        setSubcategoryImage("");
        setSelectedSubcategory("");
        setContent("");
      }
    } catch (error) {
      console.error("Error adding sub-small category:", error);
      alert("Failed to add sub-small category");
    }
  };

  return (
    <div className="category-container container mt-5 mb-4">
      <div className="row justify-content-center m-4">
        <div className="col-lg-6">
          <div className="card px-4 py-3 shadow-sm">
            <h2 className="text-center mb-3">Add a Sub-small Category</h2>
            <form onSubmit={handleSubmit}>

              {/* 🔹 Category Dropdown */}
              <div className="mb-3">
                <label className="form-label">Select Category</label>
                <select
                  className="form-control"
                  value={selectedCategory}
                  onChange={(e) => {
                    setSelectedCategory(e.target.value);
                    fetchSubcategories(e.target.value); // ✅ Fetch Subcategories
                  }}
                  required
                >
                  <option value="">Choose a Category</option>
                  {categories.map((cat) => (
                    <option key={cat._id} value={cat._id}>{cat.name}</option>
                  ))}
                </select>
              </div>

              {/* 🔹 Subcategory Dropdown */}
              <div className="mb-3">
                <label className="form-label">Select Subcategory</label>
                <select
                  className="form-control"
                  value={selectedSubcategory}
                  onChange={(e) => {
                    setSelectedSubcategory(e.target.value);
                    fetchSubSmallCategories(e.target.value); // ✅ Fetch Sub-small Categories
                  }}
                  required
                >
                  <option value="">Choose a Subcategory</option>
                  {subcategories.map((sub) => (
                    <option key={sub._id} value={sub._id}>{sub.subcategoryName}</option>
                  ))}
                </select>
              </div>

              {/* 🔹 Sub-small Category Dropdown */}
              <div className="mb-3">
                <label className="form-label">Select Sub-small Category</label>
                <select className="form-control">
                  <option value="">Choose a Sub-small Category</option>
                  {subSmallCategories.map((subsmall) => (
                    <option key={subsmall._id} value={subsmall._id}>
                      {subsmall.subSmallCategoryName}
                    </option>
                  ))}
                </select>
              </div>

              <div className="mb-3">
                <label className="form-label">SubCategory Name</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Enter subcategory name"
                  value={subcategoryname}
                  onChange={(e) => setSubcategoryName(e.target.value)}
                  required
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Image </label>
                <input
                  type="file"
                  className="form-control"
                  onChange={(e) => setPhoto(e.target.files[0])}
                  required
                />
              </div>

              <div className="mb-3">
                <label className="form-label">SubCategory Content</label>
                <textarea
                  className="form-control"
                  rows="5"
                  placeholder="Enter description..."
                  value={content}
                  onChange={(e) => setContent(e.target.value)}
                  required
                ></textarea>
              </div>

              <button 
                type="submit" 
                className="btn btn-primary w-100"
                disabled={!subcategoryimage}
              >
                Add Sub-small Category
              </button>
            </form>
          </div>
        </div>

        <div className="col-lg-6 mt-3 d-flex flex-column ">
          <img
            style={{ height: "300px", width: "400px" }}
            src={subcategoryimage || "https://via.placeholder.com/150"}
            alt="Preview"
            className="category-image img-fluid rounded shadow"
          />
        </div>
      </div>
    </div>
  );
};

export default Subsmallcategories;
