import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Viewsmallsubcategoryinfront = () => {
  const { name, subcategoryname } = useParams(); // Route se le rha hai
  console.log(name);
  const [subsmallcategories, setSmallSubcategories] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/getSubsmallcategories/${name}/${subcategoryname}`)
      .then((response) => {
        setSmallSubcategories(response.data);
      })
      .catch((error) => {
        console.error("Error fetching small subcategories:", error);
      });
  }, [name, subcategoryname]);

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4" style={{ fontWeight: "bold", color: "#333" }}>Small Subcategories of {subcategoryname}</h2>
      <div className="row">
        {subsmallcategories.map((item) => (
          <div key={item._id} className="col-md-4 mb-4">
            <div className="card shadow-sm" style={{ borderRadius: "10px", overflow: "hidden", border: "1px solid #ddd" }}>
              <img
                src={item.subsmallcategoryimage}
                className="card-img-top"
                alt={item.subsmallcategoryname}
                style={{ height: "200px", objectFit: "cover" }}
              />
              <div className="card-body text-center" style={{ backgroundColor: "#f8f9fa" }}>
                <h5 className="card-title" style={{ fontWeight: "600", color: "#007bff" }}>{item.subsmallcategoryname}</h5>
                <button 
                  className="btn btn-primary mt-2" 
                  style={{ backgroundColor: "#28a745", borderColor: "#28a745", borderRadius: "5px", padding: "8px 15px" }}
                >
                  Add to Cart
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Viewsmallsubcategoryinfront;
