import { useEffect, useState } from "react";

const Addtocart = () => {
  const [cart, setCart] = useState([]);
  const [orderPlace, setorderPlace] = useState(false);
  const [cartcount, setcartcount] = useState(0);

  const email = localStorage.getItem("email") || "guest";

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem(`cart_${email}`)) || [];
    setCart(storedCart);
  }, [email]);

  const updateCart = (updatedCart) => {
    setCart(updatedCart);
    localStorage.setItem(`cart_${email}`, JSON.stringify(updatedCart));
    localStorage.setItem("cartcount", updatedCart.length);
    window.dispatchEvent(new Event("storage"));
  };

  const handleIncreaseQuantity = (index) => {
    const updatedCart = cart.map((item, i) =>
      i === index
        ? {
            ...item,
            quantity: item.quantity + 1,
            price: (item.quantity + 1) * (item.price / item.quantity),
          }
        : item
    );
    updateCart(updatedCart);
  };

  const handleDecreaseQuantity = (index) => {
    const updatedCart = cart
      .map((item, i) =>
        i === index && item.quantity > 1
          ? {
              ...item,
              quantity: item.quantity - 1,
              price: (item.quantity - 1) * (item.price / item.quantity),
            }
          : item
      )
      .filter((item) => item.quantity > 0);

    updateCart(updatedCart);
    const newCartCount = updatedCart.length;
    setcartcount(newCartCount);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    localStorage.setItem("cartcount", newCartCount);
    window.dispatchEvent(new Event("storage"));
  };

  const handleRemoveItem = (index) => {
    const updatedCart = cart.filter((_, i) => i !== index);
    updateCart(updatedCart);
  };

  const totalAmount = cart
    .reduce((total, item) => total + parseFloat(item.price), 0)
    .toFixed(2);

  const handlePlaceOrder = async() => {
    const userId=localStorage.getItem("USER");

    if(!userId|| cart.length===0){
      alert("Invalid order data. Please log in and add items.");
      return;
    }
    try{
      const response= await fetch("http://localhost:5000/api/order",{
        method:"POST",
        headers:{
          "content-type":"Application/json",
        },
        body:JSON.stringify({
          userId,
          cartItems : cart,
          totalAmount,
        }),
      });
      const data= await response.json();
      if(response.status==201){
        alert("data.message");
// Reset cart
    localStorage.removeItem(`cart_${email}`);
    localStorage.setItem("cartcount", 0);
    setCart([]);
    setcartcount(0);
    setorderPlace(true);
  }else{
    alert(data.message || "Order not Placed");
  }
}catch(error){
  alert("Something went wrong.Please try again.");
  console.error("Order placement error", error);
}
};

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4">Your Cart</h2>
      

      {cart.length === 0 ? (
        <p className="text-center">Your cart is empty.</p>
      ) : (
        <div className="row">
          <div className="col-md-8">
            <div className="row">
              {cart.map((item, index) => (
                <div key={index} className="col-md-6 mb-4">
                  <div className="card">
                    <img
                      src={item.image}
                      className="card-img-top"
                      alt={item.name}
                    />
                    <div className="card-body">
                      <h5>{item.name}</h5>
                      <p>Price: ₹{item.price}</p>
                      <p>Quantity: {item.quantity}</p>
                      <div className="d-flex justify-content-between">
                        <button
                          className="btn btn-sm btn-success"
                          onClick={() => handleIncreaseQuantity(index)}
                        >
                          +
                        </button>
                        <button
                          className="btn btn-sm btn-warning"
                          onClick={() => handleDecreaseQuantity(index)}
                        >
                          -
                        </button>
                        <button
                          className="btn btn-sm btn-danger"
                          onClick={() => handleRemoveItem(index)}
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* ✅ Order Summary */}
          <div className="col-md-4">
            <div className="card p-3 shadow">
              <h5 className="mb-3">Order Summary</h5>
              <ul className="list-group list-group-flush">
                {cart.map((item, idx) => (
                  <li
                    key={idx}
                    className="list-group-item d-flex justify-content-between"
                  >
                    <span>
                      {item.name} × {item.quantity}
                    </span>
                    <span>₹{item.price}</span>
                  </li>
                ))}
                <li className="list-group-item d-flex justify-content-between fw-bold">
                  <span>Total</span>
                  <span>₹{totalAmount}</span>
                </li>
              </ul>
              <button
                className="btn btn-primary mt-3 w-100"
                onClick={handlePlaceOrder}
              >
                Place Order
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Addtocart;
