import { useEffect, useState } from "react";

const AdminOrders = () => {
  const [allOrders, setAllOrders] = useState([]);

  const fetchAllOrders = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/order/admin/all");
      const data = await res.json();
      console.log("All orders fetched: ", data);
      setAllOrders(data);
    } catch (err) {
      console.error("Failed to fetch all orders", err);
    }
  };

  useEffect(() => {
    fetchAllOrders();
  }, []);

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4">ðŸ“¦ All Orders (Admin View)</h2>
      {allOrders.length === 0 ? (
        <p className="text-center">No orders found.</p>
      ) : (
        allOrders.map((order, index) => (
          <div key={index} className="card mb-3 p-3 shadow-sm">
            <h5>Order #{index + 1}</h5>
            <p><strong>User:</strong> {order.userId?.name} ({order.userId?.email})</p>
            <p><strong>Date:</strong> {new Date(order.orderDate).toLocaleString()}</p>
            <p><strong>Total:</strong> â‚¹{order.totalAmount}</p>
            <p><strong>Payment:</strong> {order.paymentStatus}</p>
            <p><strong>Delivery:</strong> {order.deliveryStatus}</p>
            <ul>
              {order.cartItems.map((item, idx) => (
                <li key={idx}>{item.name} Ã— {item.quantity} = â‚¹{item.price}</li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default AdminOrders;
