import React, { useState, useEffect } from "react";
import { Outlet, Link, useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap-icons/font/bootstrap-icons.css";

const Navbar = () => {
  const [placeholderIndex, setPlaceholderIndex] = useState(0);
  const [username, setUsername] = useState(""); 
  const [role, setRole] = useState(""); 
  const navigate = useNavigate();

  useEffect(() => {
    const user = localStorage.getItem("name");
    const userRole = localStorage.getItem("role");
    if (user) {
      setUsername(user); 
      setRole(userRole?.trim().toLowerCase());
    }
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    setUsername(""); 
    setRole("");
    navigate("/login");
  };

  const placeholders = [
    "Search for 'Facial'",
    "Search for 'Kitchen Cleaning'",
    "Search for 'Home Spa'",
    "Search for 'Plumbing'",
    "Search for 'AC Repair'",
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setPlaceholderIndex((prevIndex) => (prevIndex + 1) % placeholders.length);
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <nav className="navbar navbar-expand-lg" style={{ background: "linear-gradient(to right, #F8F9FA, #E3E7EB)", boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)", padding: "12px 25px" }}>
        <div className="container-fluid">
          <a className="navbar-brand fw-bold" href="#">
            <img src="https://res.cloudinary.com/urbanclap/image/upload/t_high_res_category/w_108,dpr_2,fl_progressive:steep,q_auto:low,f_auto,c_limit/images/growth/home-screen/1687285683825-e6cf23.jpeg" alt="Logo" height="40" style={{ borderRadius: "8px" }} />
          </a>

          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse justify-content-center" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item px-3">
                <Link to="/frontblog" className="nav-link fw-bold">Blogs</Link>
              </li>
              <li className="nav-item px-3">
                <Link to="/" className="nav-link fw-bold">Category</Link>
              </li>
              <li className="nav-item px-3">
                <form className="d-flex">
                  <input className="form-control me-2" type="search" placeholder={placeholders[placeholderIndex]} style={{ borderRadius: "20px", padding: "8px 15px", border: "1px solid #B0BEC5", outline: "none", minWidth: "250px" }} />
                  <button className="btn btn-secondary" type="submit" style={{ borderRadius: "20px", fontWeight: "bold", padding: "8px 15px", backgroundColor: "#34495E", color: "white" }}>Search</button>
                </form>
              </li>
            </ul>
          </div>

          {role === "admin" ? (
            <Link to="/admin" className="nav-link fw-bold">
              <button className="btn btn-primary px-3">Admin Dashboard</button>
            </Link>
          ) : role === "user" ? (
            <Link to="/UserDashboard" className="nav-link fw-bold">
              <button className="btn btn-primary px-3">User Dashboard</button>
            </Link>
          ) : null}

          <div className="d-flex align-items-center">
            <Link to="/viewAddtocart" className="me-4" style={{ fontSize: "20px" }}>
              <i className="bi bi-cart3"></i>
            </Link>

            {username ? (
              <div className="d-flex align-items-center">
                <span style={{ width: "40px", height: "40px", borderRadius: "50%", backgroundColor: "#2C3E50", color: "white", display: "flex", alignItems: "center", justifyContent: "center", fontWeight: "bold", fontSize: "18px", marginRight: "10px" }}>{username?.charAt(0)?.toUpperCase()}</span>
                <button className="btn btn-danger ms-3" onClick={handleLogout}>Logout</button>
              </div>
            ) : (
              <Link to="/SignUp" style={{ fontSize: "20px" }}>
                <i className="bi bi-person-circle"></i>
              </Link>
            )}
          </div>
        </div>
      </nav>
      <Outlet />
    </>
  );
};

export default Navbar;
