import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";

import "./Services.css";


const Services = () => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState();
  const [show, setShow] = useState(false);

  useEffect(() => {
    fetch("http://localhost:5000/categories") // âœ… Backend se API Call
      .then((response) => response.json())
      .then((data) =>{
console.log("categories Data:", data);
       setCategories(data);
      })
      .catch((error) => console.error("Error fetching categories:", error));
  }, []);

  const handleShow = (category) => {
    setSelectedCategory(category);
    setShow(true);
  };
  //handleShow(): Jab user kisi category pe click karega, toh us category ka data store karega aur modal open hoga.


  const handleClose = () => setShow(false);

  return (
    <div>
    {/* <h2 className="text-left ms-5 mt-4 d-flex" >Expert Home Services,<strong className="d-block p-4 fs-1" > Anytime, Anywhere</strong></h2> */}

    <div className="services-container mt-5 p-4">
      <div className="categories-container">
        {categories.map((category) => (
          <div key={category._id} className="category-card" onClick={() => handleShow(category)}>
              <strong>{category.name}</strong>
              <img src={category.image}/>
          </div>
        ))}
      </div>
      

      {/* âœ… Bootstrap Modal for Subcategories */}
      
      <Modal show={show} onHide={handleClose} centered>
        {/** jb show state true hoge tb modal dikhai denga */}

        <Modal.Header closeButton>
          <Modal.Title>{selectedCategory?.name} -</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedCategory && selectedCategory.subcategories.length > 0 ? (
            //.length property check karti hai ki subcategories ka array empty hai ya nahi.
                <>             
                <ul className="list-group">
                {selectedCategory.subcategories.map((sub) => (


                <li key={sub._id} className="list-group-item">
                  {sub.subcategoryname}
                  <img src={sub.subcategoryimage} style={{ width: "50px", height: "50px", marginLeft: "10px" }}/>
                </li>
              ))}
              </ul>

              {/* if only selected particular sections*/ }
              {["AC and Appliance Repair", "Electrician,Plumber and Carpenter"].includes(selectedCategory.name) &&
  selectedCategory.sections &&
  selectedCategory.sections.map((sub) => (
    <div key={sub.title}>
      <h2>{sub.title}</h2>
      <ul>
        {/*ðŸ”¹ sub ek section object ko represent karta hai, jo ek title aur items ka array contain karta hai.*/ }
        {sub.items.map((subs) => (
          <li key={subs._id} className="list-group-item">
            {subs.name}
            <img src={subs.image} style={{ width: "50px", height: "50px", marginLeft: "10px" }} />
          </li>
        ))}
      </ul>
    </div>
  ))
}

 </>           
          ) : (
            <p>No subcategories available.</p>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

    </div>
    
    </div>
    
  );
};

export default Services;
