import React, { useState, useEffect } from 'react';

const UserOrders = () => {
  const [myOrders, setMyOrders] = useState([]);
  const userId = localStorage.getItem("USER");
useEffect(()=>{
  const fetchOrders = async () => {
       
    try {
          const res = await fetch(`http://localhost:5000/api/order/user/${userId}`);
    
          const data = await res.json();
          console.log("Fetched Orders from backend: ", data);
          if(data.success){
          setMyOrders(data.myOrders);
          }
        } catch (err) {
          console.error("Failed to fetch orders", err);
        }
      };
      if(userId) fetchOrders();
    },[userId]);
    if(!userId) return <p>Please log in to view your orders</p>;
  return (
    <div className="container my-5">
      <style>{`
        .order-card {
          background: linear-gradient(145deg, #f3f4f6, #ffffff);
          box-shadow: 0 4px 8px rgba(0,0,0,0.05);
          border-radius: 20px;
          padding: 20px 25px;
          margin-bottom: 25px;
          transition: transform 0.2s ease-in-out;
        }

        

        .order-title {
          font-size: 1.25rem;
          font-weight: 600;
          color: #2c3e50;
          margin-bottom: 10px;
        }

        .order-info p {
          margin: 4px 0;
          color: #4b5563;
        }

        .order-info strong {
          color: #1f2937;
        }

        .order-items li {
          color: #374151;
        }

        .order-status {
          font-weight: 500;
          border-radius: 12px;
          padding: 4px 12px;
          display: inline-block;
          margin-top: 8px;
        }

        .status-paid {
          background-color: #d1fae5;
          color: #065f46;
        }

        .status-pending {
          background-color: #fef3c7;
          color: #92400e;
        }

        .status-rejected {
          background-color: #fee2e2;
          color: #991b1b;
        }
      `}</style>

      {myOrders.length > 0 ? (
        <>
          <h3 className="text-center mb-4">ðŸ“¦ My Past Orders</h3>
          {myOrders.map((order, index) => (
            <div key={index} className="order-card">
              <div className="order-title">Order #{index + 1}</div>
              <div className="order-info">
                <p><strong>Date:</strong> {new Date(order.orderDate).toLocaleString()}</p>
                <p><strong>Total:</strong> â‚¹{order.totalAmount}</p>
                <p><strong>Payment:</strong> {order.paymentStatus}</p>
                <p>
                  <strong>Delivery:</strong>{" "}
                  <span className={`order-status ${
                    order.deliveryStatus === "Pending"
                      ? "status-pending"
                      : order.deliveryStatus === "Rejected"
                      ? "status-rejected"
                      : "status-paid"
                  }`}>
                    {order.deliveryStatus}
                  </span>
                </p>
              </div>
              <ul className="order-items mt-2">
                {order.cartItems.map((item, i) => (
                  <li key={i}>{item.name} Ã— {item.quantity} = â‚¹{item.price}</li>
                ))}
              </ul>
            </div>
          ))}
        </>
      ) : (
        <p className="text-center text-muted mt-4">No past orders found.</p>
      )}
    </div>
  );
};

export default UserOrders;
