import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

const Viewcategories = () => {
  const [items, setitems] = useState([]);
  const navigate = useNavigate();

  const Deletecategory = async (id) => {
    const formdata = JSON.stringify({ id: id });
    try {
      const response = await fetch(`http://localhost:5000/api/deletecategory`, {
        method: 'POST',
        headers: { 'content-type': 'application/json' },
        body: formdata
      });

      if (response.ok) {
        alert("Category deleted");
        setTimeout(() => {
          window.location.reload();
        }, 1000);
      } else {
        alert("error");
      }
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    fetch('http://localhost:5000/viewcategories')
      .then(response => response.json())
      .then(data => {
        console.log(data);
        setitems(data);
      })
      .catch(err => console.log(err));
  }, []);

  return (
    <div style={{
      marginLeft: '250px', // match your sidebar width
      width: 'calc(100% - 250px)', // take remaining space only
      padding: '20px',
      boxSizing: 'border-box',
    }}>
      <h2 className='text-center'>View categories</h2>

      <div style={{ width: '100%' }}>
        <table className="table table-dark" style={{ width: '100%', tableLayout: 'fixed' }}>
          <thead>
            <tr className="table-active">
              <th style={{ width: '15%' }}>Image</th>
              <th style={{ width: '25%' }}>Name</th>
              <th style={{ width: '25%' }}>Created at</th>
              <th style={{ width: '15%' }}>Action</th>
              <th style={{ width: '20%' }}>Update</th>
            </tr>
          </thead>
          <tbody>
            {items.map(item => (
              <tr key={item._id}>
                <td>
                  <img src={item.image} alt={item.name} style={{ maxWidth: '80px', maxHeight: '80px' }} />
                </td>
                <td>{item.name}</td>
                <td>{new Date(item.createdAt).toLocaleString()}</td>
                <td>
                  <button onClick={() => Deletecategory(item._id)}>❌</button>
                </td>
                <td>
                  <Link to={`/admin/Updatecategory/${item._id}`}>
                    <button className='btn btn-warning'>
                      <i className="fa fa-edit"></i>
                    </button>
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Add Button */}
      <button
        onClick={() => navigate('/admin/categories')}
        style={{
          marginTop: '20px',
          padding: '10px 20px',
          backgroundColor: '#28a745',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer'
        }}
      >
        Add Category
      </button>
    </div>
  );
};

export default Viewcategories;
