import React, { useEffect, useState } from "react";
import "../Blogform.css";  // Adjust relative path
 // Import scoped CSS file
import axios from "axios";
import { useParams } from "react-router-dom";

const Updatecategory = () => {
  const [name, setName] = useState("");
  const [photo, setPhoto] = useState("");
  const [photourl, setPhotourl] = useState("");
  const {id}= useParams()

 // Existing Category Data Fetch

 useEffect(() => {
    axios.post("http://localhost:5000/api/fullblog", { id })
      .then(response => {
        setName(response.data.name);
        setPhotoUrl(response.data.image);
      })
      .catch(error => console.error("Error fetching category:", error));
  }, [id]);

  useEffect(() => {
    if (photo) {
      Upload(photo);
    }
  }, [photo]);

  const uploadPhoto = async (photo) => {
    const data = new FormData();
    data.append("file", photo);
    data.append("upload_preset", "comments");
    data.append("cloud_name", "dbuk7btza");

    try {
      const response = await fetch("https://api.cloudinary.com/v1_1/dbuk7btza/image/upload", {
        method: "POST",
        body: data,
      });

      const result = await response.json();
      if (result.url) {
        setPhotoUrl(result.url);
        alert("Photo uploaded successfully");
      } else {
        alert("Failed to upload photo");
      }
    } catch (error) {
      console.error("Upload error:", error);
      alert("Error uploading photo");
    }
  }; 

  // Form Submit Handler
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(photourl);
  
    if (!name || !photourl) {
      alert("All fields are required");
      return;
    }
  
    try {
  
      const response = await axios.post("http://localhost:5000/api/updatecategory", {
      id,name, image: photourl
      });
  
      if (response.status === 200) {
        alert("Category posted successfully");
        window.location.href="/admin/categories";
        setTitle("");
        setPhoto("");
        setContent("");
      }
    } catch (error) {
      console.error("Error updating Category:", error);
      alert("Failed to update category");
    }
  };

return (
    <div className="comment-container container mt-5 mb-4">
      <div className="row justify-content-center m-4">
        {/* Comment Form */}
        <div className="col-lg-6">
          <div className="card px-4 py-3 shadow-sm">
            <h2 className="text-center mb-3">Update a Category</h2>
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label className="form-label">Category Name</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Enter comment title"
                  value={title}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Image </label>
                <input
                  type="file"
                  className="form-control"
                  onChange={(e) => setPhoto(e.target.files[0])}
                />
              </div>
              {photoUrl && (
            <div className="mb-3">
              <img src={photoUrl} alt="Category" style={{ width: "100%", maxHeight: "300px" }} />
            </div>
          )}
              <div className="mb-3">
                <label className="form-label">Blog Content</label>
                <textarea
                  className="form-control"
                  rows="5"
                  placeholder="Write your Blog..."
                  value={content}
                  onChange={(e) => setContent(e.target.value)}
                  required
                ></textarea>
              </div>

              <button type="submit" className="btn btn-primary w-100">
                Category Updated
              </button>
            </form>
          </div>
        </div>

        {/* Comment Image */}
        <div className="col-lg-6 mt-3 d-flex flex-column ">
          <img style={{height:"300px",width:"400px"}}
            src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnMGl8QAo8SpCh_HJ4aia-8agpOdGGSxbKKg&s"
            alt="Blog"
            className="comment-image img-fluid rounded shadow "
          />
          <img  style={{height:"300px",width:"400px"}}
            src="https://img.freepik.com/free-photo/online-message-comment-chat-communication-envelop-graphic-icon-concept_53876-139717.jpg"
            alt="Blog"
            className="comment-image img-fluid rounded shadow mt-4"
          />
        </div>
      </div>
    </div>
  );
};

export default Updatecategory;
