import React, { useEffect, useState } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min";

const UserLayout = () => {
    const [item, setItem] = useState({});
    const [pic, setPic] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/login");
        } else {
            const name = localStorage.getItem("name");
            const email = localStorage.getItem("email");
            setItem({ name });
            if (email) getProfilePic(email);
        }
    }, []);

    const getProfilePic = async (email) => {
        try {
            const response = await fetch("http://localhost:5000/api/get-profile", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ email }),
            });

            if (response.ok) {
                const data = await response.json();
                setPic(data.profilePic || "https://cdn-icons-png.flaticon.com/512/3135/3135715.png");
            } else {
                console.error("Failed to fetch profile picture.");
            }
        } catch (error) {
            console.error("Error fetching profile picture:", error);
        }
    };

    const handleSignOut = () => {
        localStorage.clear();
        navigate("/login");
    };

    return (
        <div className="container-fluid">
            <div className="row flex-nowrap">
                <div className="col-auto col-md-3 col-xl-2 px-sm-2 px-0 bg-dark">
                    <div className="d-flex flex-column align-items-center align-items-sm-start px-3 pt-2 text-white min-vh-100">
                        <Link to="/" className="d-flex align-items-center pb-3 mb-md-0 me-md-auto text-white text-decoration-none">
                            <span className="fs-5 d-none d-sm-inline">Menu</span>
                        </Link>
                        <ul className="nav nav-pills flex-column mb-sm-auto mb-0 align-items-center align-items-sm-start" id="menu">
                            <li className="nav-item">
                                <Link to="enquiries" className="nav-link align-middle px-0">
                                    <i className="fs-4 bi-house"></i> <span className="ms-1 d-none d-sm-inline">Home</span>
                                </Link>
                            </li>
                            <li className="nav-item dropdown">
                                <a
                                    href="#"
                                    className="nav-link px-0 align-middle dropdown-toggle"
                                    id="commentDropdown"
                                    role="button"
                                    data-bs-toggle="dropdown"
                                    aria-expanded="false"
                                >
                                    <i className="fs-4 bi-people"></i> <span className="ms-1 d-none d-sm-inline">Blogs</span>
                                </a>
                                <ul className="dropdown-menu dropdown-menu-dark text-small shadow" aria-labelledby="commentDropdown">
                                    <li><Link className="dropdown-item" to="comments">Comments</Link></li>
                                    <li><Link className="dropdown-item" to="viewcomments">View Comments</Link></li>
                                </ul>
                            </li>
                        </ul>
                        <hr />
                        <div className="dropdown pb-4">
                            <a
                                href="#"
                                className="d-flex align-items-center text-white text-decoration-none dropdown-toggle"
                                id="dropdownUser1"
                                data-bs-toggle="dropdown"
                                aria-expanded="false"
                            >
                                <img
                                    src={pic}
                                    alt="Profile"
                                    width="30"
                                    height="30"
                                    className="rounded-circle"
                                />
                                <span className="d-none d-sm-inline mx-1">{item.name}</span>
                            </a>
                            <ul className="dropdown-menu dropdown-menu-dark text-small shadow">
                                <li><Link className="dropdown-item" to="/profile">Profile</Link></li>
                                <li><hr className="dropdown-divider" /></li>
                                <li>
                                    <button className="dropdown-item" onClick={handleSignOut}>Sign out</button>
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
                <Outlet />
            </div>
        </div>
    );
};

export default UserLayout;
