import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // ✅ useNavigate import kiya
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
const Readmore = () => {
  let{id} = useParams();
  const [items, setItems] = useState([]);
  const navigate = useNavigate(); // ✅ useNavigate ko define kiya

  // Handle deletion of a comment
  const handleDelete = (id) => {
    fetch(`http://localhost:5000/deletecomment/${id}`, { method: 'DELETE' })
      .then(response => response.json())
      .then(() => {
        setItems(prevItems => prevItems.filter(item => item._id !== id)); // Ensure using `_id` for deletion
      })
      .catch(err => console.log(err));
  };

  useEffect(() => {
    const formData = JSON.stringify({
      id
    });
    fetch('http://localhost:5000/api/fullblog', {
      method: 'POST',
      headers: {
        'Content-type': 'application/json'
      },
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        console.log(data);
        setItems(data);
      })
      .catch(err => console.log(err));
  }, [id]); // Now the effect will run whenever `id` changes.
  

  return (
    <div>
      <h2 className="text-center">View Blog</h2>
      
      <div className="row">
            <div className="card mb-4">
              <img
                src={items.photo}
                alt={items.title}
                className="card-img-top"
                style={{ maxHeight: '200px', objectFit: 'cover' }} // Styling the image
              />
              <div className="card-body">
                <p className="card-text">{items.content}</p>
                    
                   
                
                 </div>
              </div>
            </div>
          </div>
      
  );
}

export default Readmore;
