import React from "react";
import "./testimonial.css"; // Optional CSS

const Testimonial = () => {
  const testimonials = [
    {
      name: "Anjali Sharma",
      feedback: "Very professional and quick service. Loved the salon experience at home!",
      image: "https://randomuser.me/api/portraits/women/65.jpg"
    },
    {
      name: "Ravi Mehta",
      feedback: "Affordable pricing and great technicians. Will book again!",
      image: "https://randomuser.me/api/portraits/men/41.jpg"
    }
  ];

  return (
    <div className="testimonial-section">
      <h3 className="text-center mb-4">What Our Customers Say</h3>
      <div className="testimonial-cards">
        {testimonials.map((t, index) => (
          <div key={index} className="testimonial-card">
            <img src={t.image} alt={t.name} className="testimonial-image" />
            <p className="testimonial-feedback">“{t.feedback}”</p>
            <h5 className="testimonial-name">— {t.name}</h5>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Testimonial;
