import React, { useEffect, useState } from 'react';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [message, setMessage] = useState("");
  const [selectedEmail, setSelectedEmail] = useState(""); // track selected user

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/allorders");
      const data = await res.json();
      console.log(data.message);
      setOrders(data.orders || []);
      setSelectedEmail(""); // reset selection
    } catch (err) {
      console.error("Orders fetch error:", err);
    }
  };

  const fetchOrdersByEmail = async (email) => {
    try {
      const res = await fetch(`http://localhost:5000/api/ordersbyemail/${email}`);
      const data = await res.json();
      setOrders(data.orders || []);
      setSelectedEmail(email); // show selected
    } catch (err) {
      console.error("Email-wise fetch error:", err);
    }
  };

  const updateOrderStatus = async (orderId, status) => {
    try {
      const res = await fetch("http://localhost:5000/api/updateorderstatus", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ orderId, deliveryStatus: status })
      });

      const data = await res.json();

      if (res.status === 200) {
        setMessage(`Order ${status} successfully!`);
        // update locally
        setOrders(prevOrders =>
          prevOrders.map(order =>
            order._id === orderId
              ? { ...order, deliveryStatus: status }
              : order
          )
        );
      } else {
        setMessage(data.message || "Failed to update order");
      }
    } catch (err) {
      console.error("Status update error:", err);
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-3">
        {selectedEmail ? `Orders by: ${selectedEmail}` : "Pending Orders"}
      </h2>

      {selectedEmail && (
        <button className="btn btn-secondary mb-3" onClick={fetchOrders}>
          ⬅ Back to All Orders
        </button>
      )}

      {message && (
        <div className="alert alert-info text-center">{message}</div>
      )}

      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        orders.map((order, index) => (
          <div key={index} className="card mb-3 p-3 shadow-sm">
            <p><strong>Service:</strong> {order.cartItems[0]?.serviceName || order.cartItems[0]?.name}</p>
            <p><strong>Date:</strong> {new Date(order.serviceDate).toLocaleDateString()}</p>
            <p><strong>Amount:</strong> ₹{order.totalAmount}</p>
            <p>
              <strong>User:</strong>{" "}
              {order.userId?.name || "N/A"} (
              <span
                onClick={() => fetchOrdersByEmail(order.userId?.email)}
                style={{ color: "blue", cursor: "pointer", textDecoration: "underline" }}
              >
                {order.userId?.email || "N/A"}
              </span>)
            </p>
            <p><strong>Status:</strong> {order.deliveryStatus}</p>

            {order.deliveryStatus === "Pending" ? (
              <div className="d-flex gap-2">
                <button
                  className="btn btn-success btn-sm"
                  onClick={() => updateOrderStatus(order._id, "Approved")}
                >
                  Approve
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => updateOrderStatus(order._id, "Rejected")}
                >
                  Reject
                </button>
              </div>
            ) : (
              <span
                className={`badge ${
                  order.deliveryStatus === "Approved" ? "bg-success" : "bg-danger"
                }`}
              >
                {order.deliveryStatus}
              </span>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default AdminOrders;
