import { useEffect, useState } from "react";

const AdminOrders = () => {
  const [allOrders, setAllOrders] = useState([]);
  const [loading, setLoading] = useState(true);


  const fetchAllOrders = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/allorders");
      const data = await res.json();
      console.log("Fetched orders:", data); // 🔍 Debug this
      setAllOrders(data);
      setLoading(false);
    } catch (err) {
      console.error("Failed to fetch all orders", err);
      setLoading(false);
    }
  };
  

  useEffect(() => {
    fetchAllOrders();
  }, []);

  if (role !== "admin") {
    return <div className="text-center text-danger mt-5">⛔ Unauthorized Access</div>;
  }

  return (
    <div className="container my-5">
      <style>{`
        .order-card {
          border-radius: 12px;
          background: #ffffff;
          box-shadow: 0 6px 12px rgba(0, 0, 0, 0.08);
          padding: 20px;
          margin-bottom: 20px;
        }
        .order-header {
          font-size: 1.2rem;
          font-weight: 600;
          color: #2c3e50;
        }
        .badge {
          border-radius: 12px;
          padding: 4px 10px;
          font-size: 0.85rem;
          font-weight: 500;
        }
        .badge-success {
          background-color: #2ecc71;
          color: white;
        }
        .badge-warning {
          background-color: #f39c12;
          color: white;
        }
        .badge-danger {
          background-color: #e74c3c;
          color: white;
        }
      `}</style>

      <h2 className="text-center mb-4">📦 All Orders (Admin View)</h2>

      {loading ? (
        <p className="text-center">Loading orders...</p>
      ) : allOrders.length === 0 ? (
        <p className="text-center">No orders found.</p>
      ) : (
        allOrders.map((order, index) => (
          <div key={index} className="order-card">
            <div className="d-flex justify-content-between align-items-center mb-2">
              <span className="order-header">Order #{index + 1}</span>
              <span className="text-muted">
                {new Date(order.orderDate).toLocaleString()}
              </span>
            </div>
            <p>
              <strong>User ID:</strong> {order.userId}
            </p>
            <p>
              <strong>Total Amount:</strong> ₹{order.totalAmount}
            </p>
            <p>
              <strong>Payment Status: </strong>
              <span
                className={
                  order.paymentStatus === "Paid"
                    ? "badge badge-success"
                    : "badge badge-warning"
                }
              >
                {order.paymentStatus}
              </span>
            </p>
            <p>
              <strong>Delivery Status: </strong>
              <span
                className={
                  order.deliveryStatus === "Delivered"
                    ? "badge badge-success"
                    : order.deliveryStatus === "Pending"
                    ? "badge badge-warning"
                    : "badge badge-danger"
                }
              >
                {order.deliveryStatus}
              </span>
            </p>
            <h6>Items:</h6>
            <ul>
              {order.cartItems.map((item, i) => (
                <li key={i}>
                  {item.name} × {item.quantity} = ₹{item.price}
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default AdminOrders;
