import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const [userCount, setUserCount] = useState(0);
  const [blogCount, setBlogCount] = useState(0); 
  const [orderStatusCount, setOrderStatusCount] = useState({
    Pending: 0,
    Shipped: 0,
    Delivered: 0,
    Rejected: 0,
  });

  useEffect(() => {
    const fetchUserCount = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/count-users");
        const data = await res.json();
        setUserCount(data.count);
        localStorage.setItem("userCount", data.count); // optional
      } catch (err) {
        console.error("Error fetching user count", err);
      }
    };
    const fetchBlogCount = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/count-blogs");
        const data = await res.json();
        setBlogCount(data.count);
      } catch (err) {
        console.error("Error fetching blog count", err);
      }
    };
    const fetchOrderStatusCount = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/order-status-count");
        const data = await res.json();
        setOrderStatusCount(data);
      } catch (err) {
        console.error("Error fetching order status count", err);
      }
    };
  
    fetchOrderStatusCount();
    fetchUserCount();
    fetchBlogCount(); 
  }, []);

  return (
    <div style={{ padding: "20px", minHeight: "100vh", overflowY: "auto" }}>
      <h2>Admin Dashboard</h2>

      <div className="row mt-4" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around' }}>
        {/* User Count Card */}
        <div className="col-12 col-sm-6 col-md-4 mb-3" style={{ padding: "10px" }}>
          <div className="card shadow-sm" style={{ backgroundColor: "#e3f2fd", maxWidth: "350px", width: "100%" }}>
            <div className="card-body text-center">
              <h5 className="card-title">Total Users</h5>
              <p className="card-text display-6 fw-bold">{userCount}</p>
              <Link to="/admin/getallusers" className="btn btn-primary mt-2">View Users</Link>
            </div>
          </div>
        </div>
        {/* Blog Count Card */}
        <div className="col-12 col-sm-6 col-md-4 mb-3" style={{ padding: "10px" }}>
          <div className="card shadow-sm" style={{ backgroundColor: "#f3e5f5", maxWidth: "350px", width: "100%" }}>
            <div className="card-body text-center">
              <h5 className="card-title">Total Blogs</h5>
              <p className="card-text display-6 fw-bold">{blogCount}</p>
              <Link to="/admin/viewblogs" className="btn btn-success mt-2">View Blogs</Link>
            </div>
          </div>
        </div>

        {/* Orders Summary */}
        <div className="col-12 col-md-6 mb-3" style={{ padding: "10px" }}>
          <div className="card shadow-sm" style={{ backgroundColor: "#fff3cd", maxWidth: "350px", width: "100%" }}>
            <div className="card-body">
              <h5 className="card-title text-center">Orders Status</h5>
              <ul className="list-group">
                <li className="list-group-item d-flex justify-content-between">
                  <span>üïí Pending:</span> <span>{orderStatusCount.Pending}</span>
                </li>
                <li className="list-group-item d-flex justify-content-between">
                  <span>üì¶ Shipped:</span> <span>{orderStatusCount.Shipped}</span>
                </li>
                <li className="list-group-item d-flex justify-content-between">
                  <span>‚úÖ Delivered:</span> <span>{orderStatusCount.Delivered}</span>
                </li>
                <li className="list-group-item d-flex justify-content-between">
                  <span>‚ùå Rejected:</span> <span>{orderStatusCount.Rejected}</span>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
