import React, { useState, useEffect } from 'react';
import { Link, Outlet, useNavigate } from 'react-router-dom';


const UserLayout = () => {
    const [item, setItem] = useState({});
    const [pic, setPic] = useState(localStorage.getItem('profile') || "https://cdn-icons-png.flaticon.com/512/3135/3135715.png");
    const [userName, setUserName] = useState('');
    const navigate = useNavigate();

    const email = localStorage.getItem('email'); // Get email from localStorage

    useEffect(() => {
        // Bootstrap dropdown enable karne ke liye
        const dropdownElements = document.querySelectorAll('.dropdown-toggle');
        dropdownElements.forEach(dropdown => {
          new window.bootstrap.Dropdown(dropdown);
        });
    
        // LocalStorage se user info fetch karna
        const email = localStorage.getItem("email");
        if (email) {
          // Backend se user profile fetch karna
          getUserProfile(email);
        }
      }, []);

    const getUserProfile = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/get-user", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ email }),
            });

            if (response.ok) {
                const data = await response.json();
                setItem({ name: data.name });
                setPic(data.profilePic);
              } else {
                console.error("Failed to fetch profile from backend");
              }
            } catch (error) {
              console.error("Error fetching user profile:", error);
            }
          };
    

    const handleSignOut = () => {
        // LocalStorage se data remove karna
        localStorage.removeItem("email");
        localStorage.removeItem("role");
        localStorage.removeItem("profile");

        // User ko login page pe redirect karna
        navigate("/login");
    };

    return (
        <div className="container-fluid">
          <div className="row flex-nowrap">
            <div className="col-auto col-md-3 col-xl-2 px-sm-2 px-0 bg-dark">
              <div className="d-flex flex-column align-items-center align-items-sm-start px-3 pt-2 text-white min-vh-100">
                <a href="/" className="d-flex align-items-center pb-3 mb-md-0 me-md-auto text-white text-decoration-none">
                  <span className="fs-5 d-none d-sm-inline">Menu</span>
                </a>
                <ul className="nav nav-pills flex-column mb-sm-auto mb-0 align-items-center align-items-sm-start" id="menu">
                  <li className="nav-item">
                    <Link to="enquiries" className="nav-link align-middle px-0">
                      <i className="fs-4 bi-house"></i> <span className="ms-1 d-none d-sm-inline">Home</span>
                    </Link>
                  </li>
                  <li>
                    <a href="#submenu1" data-bs-toggle="collapse" className="nav-link px-0 align-middle">
                      <i className="fs-4 bi-speedometer2"></i> <span className="ms-1 d-none d-sm-inline">Dashboard</span> 
                    </a>
                    <ul className="collapse show nav flex-column ms-1" id="submenu1" data-bs-parent="#menu">
                      <li className="w-100">
                        <a href="#" className="nav-link px-0"> <span className="d-none d-sm-inline">Item</span> 1 </a>
                      </li>
                      <li>
                        <a href="#" className="nav-link px-0"> <span className="d-none d-sm-inline">Item</span> 2 </a>
                      </li>
                    </ul>
                  </li>
                  <li>
                    <Link to="userorders" className="nav-link px-0 align-middle">
                      <i className="fs-4 bi-table"></i> <span className="ms-1 d-none d-sm-inline">Orders</span>
                    </Link>
                  </li>
                </ul>
                <hr />
                <div className="dropdown pb-4">
                  <a href="#" className="d-flex align-items-center text-white text-decoration-none dropdown-toggle" id="dropdownUser1" data-bs-toggle="dropdown" aria-expanded="false">
                    <img 
                      src={pic || "https://cdn-icons-png.flaticon.com/512/3135/3135715.png"} 
                      alt="Profile" 
                      width="30" 
                      height="30" 
                      className="rounded-circle" 
                    />
                    <span className="d-none d-sm-inline mx-1">{item.name}</span>
                  </a>
                  <ul className="dropdown-menu dropdown-menu-dark text-small shadow">
                    <li><a className="dropdown-item" href="#">New project...</a></li>
                    <li><a className="dropdown-item" href="#">Settings</a></li>
                    <li><Link className="dropdown-item" to="profile">Profile</Link></li>
                    <li><hr className="dropdown-divider" /></li>
                    <li><a className="dropdown-item" href="#" onClick={handleSignOut}>Sign out</a></li>
                  </ul>
                </div>
              </div>
            </div>
            <div className="col py-3 bg-light" style={{ minHeight: '100vh', overflow: 'auto' }}>
              <Outlet />
            </div>
          </div>
        </div>
      );
    };
    

export default UserLayout;
