import React, { useState, useEffect, useRef } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import { useNavigate } from "react-router-dom";
import "./Services.css";

const Services = () => {
  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [show, setShow] = useState(false);
  const [search, setSearch] = useState("");
  const scrollRef = useRef(null);

  // Fetch categories on component mount
  useEffect(() => {
    fetch("http://localhost:5000/categories")
      .then((response) => response.json())
      .then((data) => {
        setCategories(data);
      })
      .catch((error) => console.error("Error fetching categories:", error));
  }, []);

  // Handle showing modal when a category is clicked
  const handleShow = (category) => {
    setSelectedCategory(category);
    setShow(true);
  };

  // Handle subcategory click and navigation
  const handlesubcategory = (subcategory) => {
    navigate(`/viewsmallcategory/${selectedCategory?.categoryname}/${subcategory.subcategoryname}`);
    setShow(false);
  };

  // Close the modal
  const handleClose = () => setShow(false);

  // Scroll left functionality
  const scrollLeft = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollLeft -= 200;
    }
  };

  // Scroll right functionality
  const scrollRight = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollLeft += 200;
    }
  };

  // Filter categories based on the search input
  const filteredCategories = categories.filter(cat =>
    cat.categoryname && cat.categoryname.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="services-container mt-5 p-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <input
          type="text"
          placeholder="üîç Search category..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="form-control w-50"
        />
        <div>
          <Button onClick={scrollLeft} variant="outline-dark" className="mx-1">‚è™</Button>
          <Button onClick={scrollRight} variant="outline-dark" className="mx-1">‚è©</Button>
        </div>
      </div>

      {/* Categories List */}
      <div className="categories-container d-flex overflow-auto" ref={scrollRef}>
        {filteredCategories.map((category) => (
          <div key={category._id} className="category-card mx-2" onClick={() => handleShow(category)}>
            <strong>{category.categoryname}</strong>
            <img src={category.categoryimage} alt={category.categoryname} />
          </div>
        ))}
      </div>

      {/* Modal to show Subcategories when a category is clicked */}
      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>{selectedCategory?.categoryname} Subcategories</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Display subcategories in a list */}
          {selectedCategory && selectedCategory.subcategories.length > 0 ? (
            <ul className="list-group">
              {selectedCategory.subcategories.map((sub) => (
                <li
                  key={sub._id}
                  className="list-group-item d-flex align-items-center justify-content-between"
                  onClick={() => handlesubcategory(sub)}
                  style={{ cursor: "pointer" }}
                >
                  {sub.subcategoryname}
                  <img
                    src={sub.subcategoryimage}
                    alt={sub.subcategoryname}
                    style={{ width: "50px", height: "50px" }}
                  />
                </li>
              ))}
            </ul>
          ) : (
            <p>No subcategories available.</p>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Services;
