import React, { useEffect, useState } from "react";
import "../Categoryform.css";  // Adjust relative path
 // Import scoped CSS file
import axios from "axios";

const Subcategories = () => {
  const [title, setTitle] = useState("");
  const [photo, setPhoto] = useState("");
  const [photourl, setPhotourl] = useState("");
 const [content,setContent] = useState("");


  useEffect(() => {
    if (photo) {
      Upload(photo);
    }
  }, [photo]);
  
  const Upload = async(photo) => {
    if (!photo) {
      alert("Please select a photo");
      return;
    }
  
    // Check if the selected file is an image
  // if (!photo.type.startsWith('image/')) {
  //   alert("Please select a valid image file");
  //   return;
  // }
    const data = new FormData();
    data.append("file", photo);
    data.append("upload_preset", "categories");
    data.append("cloud_name", "dbuk7btza");
  
    await fetch("https://api.cloudinary.com/v1_1/dbuk7btza/image/upload", {
      method: "POST",
      body: data,
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.url) {
          setPhotourl(data.url);
          localStorage.setItem("photo", data.url);
          alert("Photo uploaded successfully");
        } else {
          alert("Failed to upload photo");
        }
      })
      .catch((err) => {
        console.error("Upload error:", err);
        alert("Error uploading photo");
      });
  };
  

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(photourl);
  
    if (!title || !content || !photourl) {
      alert("All fields are required");
      return;
    }
     
    alert("Submitting Sub Category...");
  
    // Upload the image first
    // await Upload(photo);  // Directly call Upload here instead of in useEffect.
  
    // if (!photourl) {
    //   alert("Image upload failed. Please try again.");
    //   return;
    // }
  
    try {
      const formData = { title, photo: photourl, content };
  
      const response = await axios.post("http://localhost:5000/api/insertcategory", formData, {
        headers: { "Content-Type": "application/json" },
      });
  
      if (response.status === 200) {
        alert("Category added successfully");
        setTitle("");
        setPhoto("");
        setContent("");
      }
    } catch (error) {
      console.error("Error adding category:", error);
      alert("Failed to add category");
    }
  };
  
  const [categories, setCategories] = useState([]);

useEffect(() => {
  const fetchCategories = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/getcategories"); // API URL change karo apne backend ke hisaab se
      setCategories(res.data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  fetchCategories();
}, []);



return (
    <div className="category-container container mt-5 mb-4">
      <div className="row justify-content-center m-4">
        {/* Category Form */}
        
        <div className="col-lg-6">
          <div className="card px-4 py-3 shadow-sm">
            <h2 className="text-center mb-3">Add a Category</h2>
            <form onSubmit={handleSubmit}>
            <div className="mb-3">
  <label className="form-label">Select Category</label>
  <select className="form-control">
    <option value="">Choose a Category</option>
    {categories.map((cat) => (
      <option key={cat._id} value={cat._id}>
        {cat.title}
      </option>
    ))}
  </select>
</div>
              <div className="mb-3">
                <label className="form-label">Category Title</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Enter category name"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  required
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Image </label>
                <input
                  type="file"
                  className="form-control"
                  onChange={(e) => setPhoto(e.target.files[0])}
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Category Content</label>
                <textarea
                  className="form-control"
                  rows="5"
                  placeholder="Enter category name..."
                  value={content}
                  onChange={(e) => setContent(e.target.value)}
                  required
                ></textarea>
              </div>

              <button type="submit" className="btn btn-primary w-100">
                Add Category
              </button>
            </form>
          </div>
        </div>

        {/* Category Image */}
        <div className="col-lg-6 mt-3 d-flex flex-column ">
          <img style={{height:"300px",width:"400px"}}
            src="https://th.bing.com/th/id/OIP.wWghX8rRvtrHuA3cGqqjFwHaDt?rs=1&pid=ImgDetMain"
            alt="Blog"
            className="category-image img-fluid rounded shadow "
          />
          <img  style={{height:"300px",width:"400px"}}
            src="https://wpupdoot.com/wp-content/uploads/2021/10/NCT025-Add-Category-1030x579.png"
            alt="Blog"
            className="category-image img-fluid rounded shadow mt-4"
          />
        </div>
      </div>
    </div>
  );
};

export default Subcategories;
