import React, { useEffect, useState } from "react";
import "../Blogform.css";
import axios from "axios";
import { useParams } from "react-router-dom";

const UpdateCategory = () => {
  const [name, setName] = useState("");
  const [photo, setPhoto] = useState("");
  const [photoUrl, setPhotoUrl] = useState("");
  const { id } = useParams();

  // Existing Category Data Fetch
  useEffect(() => {
    axios.post("http://localhost:5000/api/fullblog", { id })
      .then(response => {
        setName(response.data.name);
        setPhotoUrl(response.data.image);
      })
      .catch(error => console.error("Error fetching category:", error));
  }, [id]);

  // Image Upload to Cloudinary
  useEffect(() => {
    if (photo) {
      uploadPhoto(photo);
    }
  }, [photo]);

  const uploadPhoto = async (photo) => {
    const data = new FormData();
    data.append("file", photo);
    data.append("upload_preset", "comments");
    data.append("cloud_name", "dbuk7btza");

    try {
      const response = await fetch("https://api.cloudinary.com/v1_1/dbuk7btza/image/upload", {
        method: "POST",
        body: data,
      });

      const result = await response.json();
      if (result.url) {
        setPhotoUrl(result.url);
        alert("Photo uploaded successfully");
      } else {
        alert("Failed to upload photo");
      }
    } catch (error) {
      console.error("Upload error:", error);
      alert("Error uploading photo");
    }
  };

  // Form Submit Handler
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !photoUrl) {
      alert("All fields are required");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/updatecategory", {
        id, name, image: photoUrl
      });

      if (response.status === 200) {
        alert("Category updated successfully");
        window.location.href = "/admin/categories";
      }
    } catch (error) {
      console.error("Error updating category:", error);
      alert("Failed to update category");
    }
  };

  return (
    <div className="container mt-5">
      <div className="card p-4 shadow-sm">
        <h2 className="text-center">Update Category</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Category Name</label>
            <input
              type="text"
              className="form-control"
              placeholder="Enter category name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Category Image</label>
            <input
              type="file"
              className="form-control"
              onChange={(e) => setPhoto(e.target.files[0])}
            />
          </div>

          {photoUrl && (
            <div className="mb-3">
              <img src={photoUrl} alt="Category" style={{ width: "100%", maxHeight: "300px" }} />
            </div>
          )}

          <button type="submit" className="btn btn-primary w-100">Update Category</button>
        </form>
      </div>
    </div>
  );
};

export default UpdateCategory;
