import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import pic1 from "../Images/right.webp";

const Home = () => {
  return (
    <div className="container mt-5">
      <div className="row align-items-center">
        {/* Left Side - Services Box */}
        <div className="col-md-6 d-flex flex-column align-items-start p-4">
          <h1 className="mb-3">Home services at your doorstep</h1>
          <div className="service-container p-4 rounded shadow-sm">
            <h5 className="mb-3">What are you looking for?</h5>
            <div className="d-grid gap-3">
              <div className="row g-3">
                {[  
                  { img: "https://cdn-icons-png.flaticon.com/512/2867/2867281.png", name: "Women's Salon & Spa" },
                  { img: "https://cdn-icons-png.flaticon.com/512/2867/2867316.png", name: "Men's Salon & Massage" },
                  { img: "https://cdn-icons-png.flaticon.com/512/2867/2867294.png", name: "AC & Appliance Repair" },
                  { img: "https://cdn-icons-png.flaticon.com/512/2867/2867303.png", name: "Cleaning" },
                  { img: "https://cdn-icons-png.flaticon.com/512/2867/2867310.png", name: "Electrician, Plumber & Carpenter" },
                  { img: "https://cdn-icons-png.flaticon.com/512/2867/2867325.png", name: "Water Purifier Repair" },
                  { img: "https://cdn-icons-png.flaticon.com/512/2867/2867327.png", name: "Smart Locks" },
                  { img: "https://cdn-icons-png.flaticon.com/512/2867/2867341.png", name: "Painting & Renovation" },
                  { img: "https://cdn-icons-png.flaticon.com/512/2867/2867350.png", name: "Home Deep Cleaning" },
                ].map((service, index) => (
                  <div key={index} className="col-4 text-center">
                    <div className="service-box p-2 rounded shadow-sm">
                      <img src={service.img} alt={service.name} className="img-fluid" style={{ width: "50px" }} />
                      <p className="mt-2 small">{service.name}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
        
        {/* Right Side - Image Section */}
        <div className="col-md-6">
          <div className="grid-container">
            <img src={pic1} alt="Home Services" className="img-fluid rounded grid-item large" />
            <img src={pic1} alt="Home Services" className="img-fluid rounded grid-item small" />
            <img src={pic1} alt="Home Services" className="img-fluid rounded grid-item small" />
            <img src={pic1} alt="Home Services" className="img-fluid rounded grid-item small" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
