import React, { useEffect, useState } from 'react';

import { Link } from 'react-router-dom';
const getAllUser = () => {
  const [items, setitems] = useState([]);


  // Handle deletion of a comment
//   const Deletecategory = async(id) => {
//     const formdata=JSON.stringify({
//       id:id
//     })
//     try{
//     const response = await fetch(`http://localhost:5000/api/deleteblog`,
//        { method: 'POST' ,
//         headers:{'content-type':'application/json'},
//         body:formdata
//        })
//        if(response.ok){
//         alert("Category deleted");
//         setTimeout(()=>{
// window.location.reload();
//         },1000);
//        }
//        else{
// alert("error");
//        }
//     }
//     catch(err){
//       console.log(err);
//     }
//   };

  useEffect(() => {
    fetch('http://localhost:5000/api/viewusers') // Make sure this matches your backend API route
      .then(response => response.json())
      .then(data => {
        console.log(data); // Log the data to inspect it
        setitems(data);
      })
      .catch(err => console.log(err));
  }, []);
  

  return (
    <div>
      <h2 className='text-center'>View Users</h2>
      <table className="table table-dark">
        <tbody>
          <tr className="table-active">
            
            <th>Username</th>
            <th>Image</th>
            <th>Useremail</th>
            <th>Created at</th>
           
           
          </tr>
          {
            items.map(item => (
              <tr key={item._id}>
                <td><img src={item.image} alt={item.title} style={{ maxWidth: '100px', maxHeight: '100px' }} /></td>
                <td>{item.name}</td>
               
                <td>{item.createdAt}</td>
                
               
              </tr>
            ))
          }
        </tbody>
      </table>

      
    </div>
    
  );
}

export default getAllUser;
